import React, { useState } from 'react';
import {
  Avatar,
  Button,
  CssBaseline,
  TextField,
  FormControl,
  FormControlLabel,
  Checkbox,
  FormHelperText,
  Grid,
  Box,
  Typography,
  Container,
  Stack,
  IconButton,
} from '@mui/material/';
import SearchIcon from '@mui/icons-material/Search';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns'

const RegisterPage = () => {
  const theme = createTheme();
  const [checked, setChecked] = useState(false);
  const [nameError, setNameError] = useState('');
  const [idError, setIdError] = useState('');
  const [pwError, setPwError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [selectedDate, setSelectedDate] = useState(null);
  const [birthError, setBirthError] = useState('');

  // 동의 체크
  const handleAgree = (event) => {
    setChecked(event.target.checked);
  };

  // 생년월일 선택
  const handleDateChange = (date) => {
    setSelectedDate(date);
    setBirthError('');
  };

// Date 객체를 'yyyy-mm-dd' 형식의 문자열로 변환하는 함수
const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

  // form 전송
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedDate) {
      setBirthError('생년월일을 선택해주세요.');
      return;
    }
    const data = new FormData(e.currentTarget);
   
    // 생년월일을 'yyyy-mm-dd' 형식으로 변환
   const formattedDate = selectedDate ? formatDate(selectedDate) : '';


    const regData = {
      id: data.get('id'),
      pw: data.get('pw'),
      rePw: data.get('rePw'),
      name: data.get('name'),
      birth: formattedDate, 
      email: data.get('email'),
    };
    console.log(regData);

    const { id,pw,rePw,name,birth,email } = regData;
    
    {/* id 중복 체크 / 유효성 검사 */}
    const handleCheckDuplicateId = () => {
      // 중복 확인 로직 처리
      const id = document.getElementById('id').value;
      // ID 유효성 검사
      const idRegex = /^[A-Za-z][A-Za-z0-9]{5,}$/;
      if (!idRegex.test(id)) {
      setIdError('영문자로 시작하고, 영문자와 숫자의 조합으로 6자리 이상이어야 합니다.');
      return;
    }
  
    // 중복 확인 로직 수행
    // ...
  
    // ID가 중복되지 않은 경우
    setIdError('');
    };
  };






  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 2, bgcolor: 'primary.main' }} />

          <Typography component="h1" variant="h5">
            SF-DB 회원가입
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <FormControl component="fieldset" variant="standard">

              
              <Grid container spacing={2}>
              
                <Grid item xs={12} sm={10}>
                  <TextField required fullWidth id="id" name="id" label="아이디" />
                </Grid>


               <Grid item xs={12} sm={2}>
  <IconButton onClick={handleCheckDuplicateId}>
    <SearchIcon />
  </IconButton>
  {idError && <FormHelperText error>{idError}</FormHelperText>}
</Grid>

                
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="password"
                    id="pw"
                    name="pw"
                    label="비밀번호 (숫자+영문자+특수문자 8자리 이상)"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="password"
                    id="rePw"
                    name="rePw"
                    label="비밀번호 확인"
                  />
                </Grid>
           

<Grid item xs={12} sm={6}>
                  <TextField required autoFocus fullWidth id="name" name="name" label="이름" />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <DatePicker
                      label="생년월일"
                      value={selectedDate}
                      onChange={handleDateChange}
                      renderInput={(params) => <TextField {...params} error={birthError !== ''} helperText={birthError}  />}
                    />
                  </LocalizationProvider>
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="email"
                    id="email"
                    name="email"
                    label="이메일 주소"
                  />
                </Grid>

                <Grid item xs={12}>
                  <FormControlLabel
                    control={<Checkbox onChange={handleAgree} color="primary" />}
                    label="회원가입 약관에 동의합니다."
                  />
                </Grid>
              </Grid>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, bgcolor: 'primary.main' }}
                size="large"
              >
                가입하기
              </Button>
            </FormControl>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default RegisterPage;
