  import React, { useState } from 'react';
  import {
    Avatar,
    Button,
    CssBaseline,
    TextField,
    FormControl,
    FormControlLabel,
    Checkbox,
    FormHelperText,
    Grid,
    Box,
    Typography,
    Container,
    Stack,
    IconButton,
  } from '@mui/material/';
  import SearchIcon from '@mui/icons-material/Search';
  import { createTheme, ThemeProvider } from '@mui/material/styles';
  import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers';
  import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns'
import { useNavigate } from 'react-router';
import { styled } from 'styled-components';
import { set } from 'date-fns';
import { idCheckDB, regInsertDB } from '../../axios/user/registerLogic';

// mui의 css 우선순위가 높기때문에 important를 설정 - 실무하다 보면 종종 발생 우선순위 문제
const FormHelperTexts = styled(FormHelperText)`
width: 100%;
padding-left: 16px;
font-weight: 700 !important;
color: #d32f2f !important;
`;

const Boxs = styled(Box)`
padding-bottom: 40px !important;
`;

  const RegisterPage = () => {
    const theme = createTheme();
    const navigate = useNavigate();
    
    const [checked, setChecked] = useState(false);
    const [id, setId] = useState('');
    const [idError, setIdError] = useState('');
    const [pw, setPw] = useState('');
    const [rePw, setRePw] = useState('');
    const [pwError, setPwError] = useState('');
    const [nameError, setNameError] = useState('');
    const [selectedDate, setSelectedDate] = useState(null);
    const [birthError, setBirthError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [registerError, setRegisterError] = useState('');
   

  /************************************************************************************************/
  /* 함수 정의 */
  
  //id 중복확인 기능
  const handleCheckDuplicateId = async(userId) => {
    console.log(userId);
    // 중복 확인 로직 처리
    try {
      const res = await idCheckDB(userId);
      console.log(res);
      if(res){
        setIdError('이미 존재하는 아이디입니다.');
      }else{
        alert('사용 가능한 아이디입니다.');
        setIdError('');
      }
    } catch (error) {
      console.log(error);
      setIdError('다시 시도해주세요.')
    }

  }
   

  //ID찾기 버튼 클릭 시 'id'입력값을 받아야함
  const handleSearchButtonClick = () => {
    const userId = document.getElementById('id').value;
    handleCheckDuplicateId(userId);
  }
  


  
  // 생년월일 선택
    const handleDateChange = (date) => {
      setSelectedDate(date);
      setBirthError('');
    };
  // Date 객체를 'yyyy-mm-dd' 형식의 문자열로 변환하는 함수
  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };
  
  
  // 동의 체크
    const handleAgree = (event) => {
      setChecked(event.target.checked);
    };
  
  

/************************************************************************************************/
/* 회원가입 데이터 요청 */
const regDataSend = async (data) => {
  try {
    const res = await regInsertDB(data)
    console.log(res.data);
    if(res.data){
     navigate('./login')
     alert('회원가입이 완료되었습니다. 로그인 해주세요.')
    }else{
    console.log('가입 실패');
    alert('회원가입에 실패하였습니다.')
    }
  } catch (error) {
    console.log(error);
    alert('다시 시도해주세요.');
}
}
  
const handleSubmit = async (e) => {
  e.preventDefault();
  if (!selectedDate) {
    setBirthError('생년월일을 선택해주세요.');
    return;
  }


  const data = new FormData(e.currentTarget);
  const { id, pw, rePw, name, birth, email } = Object.fromEntries(data.entries());

  // 중복 아이디 체크
  try {
    const res = await handleCheckDuplicateId(id);
    console.log(res.data);
    if (res.data.duplicate) {
      setIdError('이미 존재하는 아이디입니다.');
      return;
    } else {
      alert('사용 가능한 아이디입니다.');
      setIdError('');
    }
  } catch (error) {
    console.log(error);
    setIdError('다시 시도해주세요.');
    return;
  }
  // 입력값에 빈 칸이 있는지 확인
  if (!id || !pw || !rePw || !name || !email) {
    alert('입력하지 않는 부분을 확인해주세요.');
    return;
  }

  // 유효성 검사
  const idRegex = /^[A-Za-z][A-Za-z0-9]{5,}$/;
  const pwRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
  const nameRegex = /^[a-zA-Z가-힣]+$/;
  const emailRegex = /([\w-.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/;

  if (!idRegex.test(id)) {
    setIdError('영문자로 시작하고, 영문자와 숫자의 조합으로 6자리 이상이어야 합니다.');
    return;
  } else {
    setIdError('');
  }

  if (!pwRegex.test(pw)) {
    setPwError('영문자, 숫자, 특수 문자의 조합으로 8자리 이상이어야 합니다.');
    return;
  } else {
    setPwError('');
  }

  if (pw !== rePw) {
    setPwError('비밀번호가 일치하지 않습니다.');
    return;
  } else {
    setPwError('');
  }

  if (!nameRegex.test(name)) {
    setNameError('이름은 한글 또는 영문으로 입력해주세요.');
    return;
  } else {
    setNameError('');
  }

  if (!emailRegex.test(email)) {
    setEmailError('올바른 이메일 형식이 아닙니다.');
    return;
  } else {
    setEmailError('');
  }

  if (!checked) {
    alert('회원가입 약관에 동의해주세요.');
    return;
  }

  // 회원가입 데이터 요청
  const formattedDate = selectedDate ? formatDate(selectedDate) : '';
  const regData = {
    userId : id,
    userPw: pw,
    userName : name,
    userBirth: formattedDate,
    userEmail : email,
  };

  regDataSend(regData);
};






