    return (
      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 8,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 2, bgcolor: 'primary.main' }} />

            <Typography component="h1" variant="h5">
              SF-DB 회원가입
            </Typography>
            <Boxs component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
              <FormControl component="fieldset" variant="standard">
                
                <Grid container spacing={2}>
{/* 아이디 입력 및 중복 확인 */}
                  <Grid item xs={12} sm={10}>
                    <TextField required autoFocus fullWidth id="id" name="id" label="아이디" value={id} onChange={(e) => setId(e.target.value)}/>
                    {idError && <FormHelperTexts error>{idError}</FormHelperTexts>}
                  </Grid>
                <Grid item xs={12} sm={2}>
    <IconButton onClick={handleSearchButtonClick}>
      <SearchIcon />
    </IconButton>
   
  </Grid>
  

{/* 비밀번호 입력 및 재확인 */}                  
                  <Grid item xs={12}>
                    <TextField
                      required
                      fullWidth
                      type="password"
                      id="pw"
                      name="pw"
                      label="비밀번호 (숫자+영문자+특수문자 8자리 이상)"
                      value={pw}
                      onChange={(e) => {
                        const value = e.target.value;
                        setPw(value);
                        // 비밀번호 변경 시, 비밀번호 확인과 일치 여부 확인
                        setPwError(value !== rePw ? '비밀번호가 일치하지 않습니다.' : '');
                      }}
                      error={pwError !== ''}
                      helperText={pwError}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      required
                      fullWidth
                      type="password"
                      id="rePw"
                      name="rePw"
                      label="비밀번호 확인"
                      value={rePw}
                      onChange={(e)=>{
                        const value = e.target.value;
                        setRePw(value)
                         // 비밀번호 확인 변경 시, 비밀번호와 일치 여부 확인
                      setPwError(value !== pw ? '비밀번호가 일치하지 않습니다.' : '');
                      }} 
                      error={pwError !== ''}
                      helperText={pwError}
                      InputProps={{
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              onClick={handleShowPassword}
                              onMouseDown={(e) => e.preventDefault()}
                              edge="end"
                            >
                              {showPassword ? <VisibilityOff /> : <Visibility />}
                            </IconButton>
                          </InputAdornment>
                        ),
                      }}
                    />
                  </Grid>
            

{/* 이름 및 생년월일 입력 */}
  <Grid item xs={12} sm={6}>
                    <TextField required fullWidth id="name" name="name" label="이름"
                     error={nameError !== ''}
  helperText={nameError} />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <LocalizationProvider dateAdapter={AdapterDateFns}>
                      <DatePicker
                        label="생년월일"
                        value={selectedDate}
                        onChange={handleDateChange}
                        renderInput={(params) => <TextField {...params} error={birthError !== ''} helperText={birthError}  />}
                      />
                    </LocalizationProvider>
                  </Grid>

{/* 이메일 입력 */}
                  <Grid item xs={12}>
                    <TextField
                      required
                      fullWidth
                      type="email"
                      id="email"
                      name="email"
                      label="이메일 주소"
                      error={emailError !== ''}
                      helperText={emailError}
                    />
                  </Grid>

{/* 약관 동의 */}
                  <Grid item xs={12}>
                    <FormControlLabel
                      control={<Checkbox onChange={handleAgree} color="primary" />}
                      label="회원가입 약관에 동의합니다."
                    />
                  </Grid>
                </Grid>
             {/* 가입 요청 */}
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  sx={{ mt: 3, mb: 2, bgcolor: 'primary.main' }}
                  size="large"
                >
                  가입하기
                </Button>
              </FormControl>
              <FormHelperTexts>{registerError}</FormHelperTexts>
              </Boxs>
          </Box>
        </Container>
      </ThemeProvider>
    );
  };

  export default RegisterPage;
