import * as React from "react";
import Box from "@mui/material/Box";
import Link from "@mui/material/Link";
import AppBar from "../components/AppBar";
import Toolbar from "../components/Toolbar";
import SearchBar from "./SearchBar";
import MenuIcon from "@mui/icons-material/Menu";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import { IconButton } from "@mui/material";

const rightLink = {
  fontSize: 18,
  color: "white",
  ml: 3,
  lineHeight: "1",
  padding: "8px 12px",
};

function HeaderBar() {
  const [isSidebarOpen, setIsSidebarOpen] = React.useState(false);

  const handleMenuIconClick = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleSidebarClose = () => {
    setIsSidebarOpen(false);
  };

  return (
    <div>
      <AppBar position="fixed">
        <Toolbar sx={{ justifyContent: "space-between" }}>
          {/* 메뉴 아이콘과 텍스트를 같은 높이로 맞춤 */}
          <Box sx={{ flex: 1, display: "flex", alignItems: "center" }}>
            <IconButton sx={{color:'white'}} onClick={handleMenuIconClick}>
            <MenuIcon />
            {"MENU"}
            </IconButton>
          </Box>

          {/* 로고와 "SFDB"를 정중앙에 정렬 */}
          <Box sx={{ flex: 1, display: "flex", justifyContent: "center" }}>
            <img
              src="/images/SF-DBW.png"
              alt="SF-DB 로고"
              style={{ width: "50px", height: "50px", marginRight: "10px" }}
            />
            <Link
              variant="h6"
              underline="none"
              color="inherit"
              href="/"
              sx={{ fontSize: 24 }}
            >
              {"SFDB"}
            </Link>
          </Box>

          <Box sx={{ flex: 1, display: "flex", justifyContent: "flex-end" }}>
            {/* 검색창 */}
            <Box>
              <SearchBar />
            </Box>

            {/* 로그인 링크 */}
            <Link
              color="inherit"
              variant="h6"
              underline="none"
              href="/login"
              sx={rightLink}
            >
              {"로그인"}
            </Link>

            {/* 가입하기 링크 */}
            <Link
              variant="h6"
              underline="none"
              href="/register"
              sx={{ ...rightLink, color: "science.main" }}
            >
              {"가입하기"}
            </Link>
          </Box>
        </Toolbar>
      </AppBar>

       {/* 메뉴바 */}
       <Drawer
        anchor="left"
        open={isSidebarOpen}
        onClose={handleSidebarClose}
        variant="temporary"
        sx={{ width: "100vw" }}
      >
        <List>
          {/* 여기에 사이드바 메뉴 아이템들을 추가합니다. */}
          <ListItem>
            <ListItemText primary="사이드바 메뉴 1" />
          </ListItem>
          <ListItem>
            <ListItemText primary="사이드바 메뉴 2" />
          </ListItem>
          {/* 추가적인 메뉴 아이템들을 원하시면 위와 같이 ListItem 컴포넌트를 사용하여 추가합니다. */}
        </List>
      </Drawer>

      <Toolbar />
    </div>
  );
}

export default HeaderBar;
