import * as React from "react";
import Box from "@mui/material/Box";
import Link from "@mui/material/Link";
import AppBar from "../components/AppBar";
import Toolbar from "../components/Toolbar";
import SearchBar from "./SearchBar";
import MenuIcon from "@mui/icons-material/Menu";

const rightLink = {
  fontSize: 16,
  color: "white",
  ml: 3,
  lineHeight: "1", // 높이를 낮추기 위해 lineHeight 값 설정
};

function HeaderBar() {
  return (
    <div>
      <AppBar position="fixed">
        <Toolbar sx={{ justifyContent: "space-between" }}>
          {/* 메뉴 아이콘과 텍스트를 같은 높이로 맞춤 */}
          <Box sx={{ flex: 1, display: "flex", alignItems: "center" }}>
            <MenuIcon />
            {"MENU"}
          </Box>

          {/* 로고와 "SFDB"를 정중앙에 정렬 */}
          <Box sx={{ flex: 1, display: "flex", justifyContent: "center" }}>
            <img
              src="/images/SF-DBW.png"
              alt="SF-DB 로고"
              style={{ width: "50px", height: "50px", marginRight: "10px" }}
            />
            <Link
              variant="h6"
              underline="none"
              color="inherit"
              href="/"
              sx={{ fontSize: 24 }}
            >
              {"SFDB"}
            </Link>
          </Box>

          <Box sx={{ flex: 1, display: "flex", justifyContent: "flex-end" }}>
            {/* 검색창 */}
            <Box>
              <SearchBar />
            </Box>

            {/* 로그인 링크 */}
            <Link
              color="inherit"
              variant="h6"
              underline="none"
              href="/login"
              sx={{ ...rightLink, lineHeight: "1" }} // 높이를 낮추기 위해 lineHeight 값 설정
            >
              {"로그인"}
            </Link>

            {/* 가입하기 링크 */}
            <Link
              variant="h6"
              underline="none"
              href="/register"
              sx={{ ...rightLink, color: "science.main", lineHeight: "1" }} // 높이를 낮추기 위해 lineHeight 값 설정
            >
              {"가입하기"}
            </Link>
          </Box>
        </Toolbar>
      </AppBar>
      <Toolbar />
    </div>
  );
}

export default HeaderBar;
